use soroban_cli::assembled::simulate_and_assemble_transaction;
use soroban_cli::xdr::{Limits, ReadXdr, TransactionEnvelope, WriteXdr};
use soroban_test::{AssertExt, TestEnv};

use crate::integration::util::{deploy_contract, DeployKind, HELLO_WORLD};

pub mod operations;

#[tokio::test]
async fn simulate() {
    let sandbox = &TestEnv::new();
    let xdr_base64_build_only =
        deploy_contract(sandbox, HELLO_WORLD, DeployKind::BuildOnly, None).await;
    let xdr_base64_sim_only =
        deploy_contract(sandbox, HELLO_WORLD, DeployKind::SimOnly, None).await;
    let tx_env =
        TransactionEnvelope::from_xdr_base64(&xdr_base64_build_only, Limits::none()).unwrap();
    let tx = soroban_cli::commands::tx::xdr::unwrap_envelope_v1(tx_env).unwrap();
    let assembled_str = sandbox
        .new_assert_cmd("tx")
        .arg("simulate")
        .write_stdin(xdr_base64_build_only.as_bytes())
        .assert()
        .success()
        .stdout_as_str();
    assert_eq!(xdr_base64_sim_only, assembled_str);
    let assembled = simulate_and_assemble_transaction(&sandbox.client(), &tx)
        .await
        .unwrap();
    let txn_env: TransactionEnvelope = assembled.transaction().clone().into();
    assert_eq!(
        txn_env.to_xdr_base64(Limits::none()).unwrap(),
        assembled_str
    );
}

#[tokio::test]
async fn txn_hash() {
    let sandbox = &TestEnv::new();

    let xdr_base64 = "";

    let expected_hash = "bcc9fa60c8f6607c981d6e1c65d77ae07617720113f9080fe5883d8e4a331a68";

    let hash = sandbox
        .new_assert_cmd("tx")
        .arg("hash")
        .write_stdin(xdr_base64.as_bytes())
        .assert()
        .success()
        .stdout_as_str();

    assert_eq!(hash.trim(), expected_hash);
}

#[tokio::test]
async fn build_simulate_sign_send() {
    let sandbox = &TestEnv::new();
    sandbox
        .new_assert_cmd("contract")
        .arg("install")
        .args(["--wasm", HELLO_WORLD.path().as_os_str().to_str().unwrap()])
        .assert()
        .success();

    let tx_simulated = deploy_contract(sandbox, HELLO_WORLD, DeployKind::SimOnly, None).await;
    dbg!("{tx_simulated}");

    let tx_signed = sandbox
        .new_assert_cmd("tx")
        .arg("sign")
        .arg("--sign-with-key=test")
        .write_stdin(tx_simulated.as_bytes())
        .assert()
        .success()
        .stdout_as_str();
    dbg!("{tx_signed}");

    sandbox
        .new_assert_cmd("tx")
        .arg("send")
        .write_stdin(tx_signed.as_bytes())
        .assert()
        .success()
        .stdout(predicates::str::contains("SUCCESS"));
}
